import { Table, flexRender } from "@tanstack/react-table"
import { TableBody, TableCell, TableRow } from "./table"
import { tv } from "tailwind-variants"

const dataTableBodySlots = tv({
  slots: {
    row: "hover:bg-muted/50 transition-colors",
  },
  variants: {
    selected: {
      true: { row: "bg-muted" },
    },
  },
})

const { row: rowSlot } = dataTableBodySlots()

interface DataTableBodyProps<TData> {
  table: Table<TData>
  noResults?: React.ReactNode
  onRowClick?: (row: TData) => void
  rowClassName?: string | ((row: TData) => string)
  cellClassName?: string | ((cell: any, row: TData) => string)
}

export function DataTableBody<TData>({
  table,
  noResults = "No results.",
  onRowClick,
  rowClassName,
  cellClassName,
}: DataTableBodyProps<TData>) {
  const rows = table.getRowModel().rows
  const hasRows = rows?.length > 0

  const getRowClass = (row: TData): string => {
    if (typeof rowClassName === "function") {
      return rowClassName(row)
    }
    return rowClassName || ""
  }

  const getCellClass = (cell: any, row: TData): string => {
    if (typeof cellClassName === "function") {
      return cellClassName(cell, row)
    }
    return cellClassName || ""
  }

  if (!hasRows) {
    return (
      <TableBody>
        <TableRow>
          <TableCell
            colSpan={table.getAllColumns().length}
            className="h-24 text-center"
          >
            {noResults}
          </TableCell>
        </TableRow>
      </TableBody>
    )
  }

  return (
    <TableBody>
      {rows.map((row) => (
        <TableRow
          key={row.id}
          data-state={row.getIsSelected() && "selected"}
          onClick={() => onRowClick?.(row.original)}
          className={rowSlot({
            selected: row.getIsSelected(),
            className: getRowClass(row.original),
          })}
        >
          {row.getVisibleCells().map((cell) => (
            <TableCell
              key={cell.id}
              className={getCellClass(cell, row.original)}
            >
              {flexRender(cell.column.columnDef.cell, cell.getContext())}
            </TableCell>
          ))}
        </TableRow>
      ))}
    </TableBody>
  )
}

import { Column } from "@tanstack/react-table"
import { ArrowDown, ArrowUp, ChevronsUpDown } from "lucide-react"

import { tv } from "tailwind-variants"

const dataTableColumnHeaderSlots = tv({
  slots: {
    base: "",
    sortable: "group flex min-w-fit items-center gap-0.5 whitespace-nowrap",
  },
})

const { base, sortable } = dataTableColumnHeaderSlots()

interface DataTableColumnHeaderProps<TData, TValue>
  extends React.HTMLAttributes<HTMLDivElement> {
  column: Column<TData, TValue>
  title: string
}

export function DataTableColumnHeader<TData, TValue>({
  column,
  title,
  className,
}: DataTableColumnHeaderProps<TData, TValue>) {
  if (!column.getCanSort()) {
    return <div className={base({ className })}>{title}</div>
  }

  return (
    <div
      className={sortable({ className })}
      onClick={() => {
        const isSorted = column.getIsSorted() === "desc"

        column.toggleSorting(isSorted ? false : true)
      }}
    >
      <span className="text-lg">{title}</span>
      <div className="opacity-0 group-hover:opacity-100">
        {column.getIsSorted() === "desc" ? (
          <ArrowUp size={20} />
        ) : column.getIsSorted() === "asc" ? (
          <ArrowDown size={20} />
        ) : (
          <ChevronsUpDown size={20} />
        )}
      </div>
    </div>
  )
}

"use client"

import { DropdownMenuTrigger } from "@radix-ui/react-dropdown-menu"
import { Table } from "@tanstack/react-table"
import { Settings2 } from "lucide-react"

import Button from "@/registry/fluent2tailwind/button/button"
import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuLabel,
  DropdownMenuSeparator,
} from "@/registry/fluent2tailwind/dropdown-menu/dropdown-menu"

interface DataTableViewOptionsProps<TData> {
  table: Table<TData>
}

export function DataTableViewOptions<TData>({
  table,
}: DataTableViewOptionsProps<TData>) {
  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button
          variant="outline"
          size="sm"
          className="ml-auto hidden h-8 lg:flex"
        >
          <Settings2 />
          View
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="end" className="w-[150px]">
        <DropdownMenuLabel>Toggle columns</DropdownMenuLabel>
        <DropdownMenuSeparator />
        {table
          .getAllColumns()
          .filter(
            (column) =>
              typeof column.accessorFn !== "undefined" && column.getCanHide()
          )
          .map((column) => {
            return (
              <DropdownMenuCheckboxItem
                key={column.id}
                className="capitalize"
                checked={column.getIsVisible()}
                onCheckedChange={(value) => column.toggleVisibility(!!value)}
              >
                {column.id}
              </DropdownMenuCheckboxItem>
            )
          })}
      </DropdownMenuContent>
    </DropdownMenu>
  )
}

import { DotsHorizontalIcon } from "@radix-ui/react-icons"
