  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/registry/fluent2tailwind/dropdown-menu/dropdown-menu"
import { User } from "@/registry/fluent2tailwind/data-table/data-table.input"
import { DataTableColumnHeader } from "./data-table.columnHeader"

function renderCell(key: string, row?: Row<User>) {
  if (row) {
    return <div className="lowercase">{row.getValue(key)}</div>
  }
  return <div className="text-primary-foreground capitalize">{key}</div>
}

function renderActions(row: Row<User>) {
  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button variant="subtle" className="h-8 w-8 p-0">
          <span className="sr-only">Open menu</span>
          <DotsHorizontalIcon className="h-4 w-4" />
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="end">
        <DropdownMenuLabel>Actions</DropdownMenuLabel>
        <DropdownMenuItem
          onClick={() => navigator.clipboard.writeText(row.getValue("id"))}
        >
          Copy payment ID
        </DropdownMenuItem>
        <DropdownMenuSeparator />
        <DropdownMenuItem>View customer</DropdownMenuItem>
        <DropdownMenuItem>View payment details</DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  )
}

