import { Input } from "@/registry/fluent2tailwind/input/input"
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/registry/fluent2tailwind/select/select"
import { Table } from "@tanstack/react-table"
import { useState } from "react"

interface DataTableFilterProps<TData> {
  table: Table<TData>
}

export function DataTableFilter<TData>({ table }: DataTableFilterProps<TData>) {
  const [selectedColumn, setSelectedColumn] = useState<string | null>(null)

  const filterableColumns = table
    .getAllColumns()
    .filter((column) => column.getCanFilter())

  const onColumnSelect = (columnId: string) => {
    setSelectedColumn(columnId)
    // Clear previous filter when changing columns
    if (selectedColumn) {
      const prevColumn = table.getColumn(selectedColumn)
      if (prevColumn) {
        prevColumn.setFilterValue("")
      }
    }
  }

  const onFilterChange = (value: string) => {
    if (selectedColumn) {
      const column = table.getColumn(selectedColumn)
      if (column) {
        column.setFilterValue(value)
      }
    }
  }

  return (
    <div className="flex gap-2">
      <Select value={selectedColumn || ""} onValueChange={onColumnSelect}>
        <SelectTrigger className="w-[180px]">
          <SelectValue placeholder="Select column to filter..." />
        </SelectTrigger>
        <SelectContent>
          {filterableColumns.map((column) => (
            <SelectItem key={column.id} value={column.id}>
              {column.id}
            </SelectItem>
          ))}
        </SelectContent>
      </Select>

      {selectedColumn && (
        <Input
          placeholder="Type to filter..."
          value={
            (table.getColumn(selectedColumn)?.getFilterValue() as string) ?? ""
          }
          onChange={(e) => onFilterChange(e.target.value)}
          className="w-[180px]"
        />
      )}
    </div>
  )
}

import { Table } from "@tanstack/react-table"
import {
  ChevronLeft,
  ChevronRight,
  ChevronsLeft,
  ChevronsRight,
} from "lucide-react"

import Button from "@/registry/fluent2tailwind/button/button"
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/registry/fluent2tailwind/select/select"

interface DataTablePaginationProps<TData> {
  table: Table<TData>
}

export function DataTablePagination<TData>({
  table,
}: DataTablePaginationProps<TData>) {
  return (
    <div className="flex items-center justify-between px-2">
      <div className="text-muted-foreground flex-1 text-sm">
        {table?.getFilteredSelectedRowModel().rows.length} of{" "}
        {table?.getFilteredRowModel().rows.length} row(s) selected.
      </div>
      <div className="flex items-center space-x-6 lg:space-x-8">
        <div className="flex items-center space-x-2">
          <p className="text-sm font-medium">Rows per page</p>
          <Select
            value={`${table.getState().pagination.pageSize}`}
            onValueChange={(value) => {
              table.setPageSize(Number(value))
            }}
          >
            <SelectTrigger className="h-8 w-[70px]">
              <SelectValue placeholder={table.getState().pagination.pageSize} />
            </SelectTrigger>
            <SelectContent side="top">
              {[10, 20, 30, 40, 50].map((pageSize) => (
                <SelectItem key={pageSize} value={`${pageSize}`}>
                  {pageSize}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
        <div className="flex w-[100px] items-center justify-center text-sm font-medium">
          Page {table.getState().pagination.pageIndex + 1} of{" "}
          {table.getPageCount()}
        </div>
        <div className="flex items-center space-x-2">
          <Button
            variant="outline"
            className="hidden h-8 w-8 p-0 lg:flex"
            onClick={() => table.setPageIndex(0)}
            disabled={!table.getCanPreviousPage()}
          >
            <span className="sr-only">Go to first page</span>
            <ChevronsLeft />
          </Button>
          <Button
            variant="outline"
            className="h-8 w-8 p-0"
            onClick={() => table.previousPage()}
            disabled={!table.getCanPreviousPage()}
          >
            <span className="sr-only">Go to previous page</span>
            <ChevronLeft />
          </Button>
          <Button
            variant="outline"
            className="h-8 w-8 p-0"
            onClick={() => table.nextPage()}
            disabled={!table.getCanNextPage()}
          >
            <span className="sr-only">Go to next page</span>
            <ChevronRight />
          </Button>
          <Button
            variant="outline"
            className="hidden h-8 w-8 p-0 lg:flex"
            onClick={() => table.setPageIndex(table.getPageCount() - 1)}
            disabled={!table.getCanNextPage()}
          >
            <span className="sr-only">Go to last page</span>
            <ChevronsRight />
          </Button>
        </div>
      </div>
    </div>
  )
}

import { ColumnDef, flexRender } from "@tanstack/react-table"
import { DataTableBody } from "./data-table.body"
import { DataTableViewOptions } from "./data-table.columnToggle"
import { DataTableFilter } from "./data-table.filter"
import { DataTablePagination } from "./data-table.pagination"
import { Table, TableHead, TableHeader, TableRow } from "./table"
import { useDataTable } from "./useDataTable"

interface DataTableProps<TData, TValue> {
  data: TData[]
  columns: ColumnDef<TData, TValue>[]
  features?: {
    sorting?: boolean
    filtering?: boolean
    pagination?: boolean
    columnVisibility?: boolean
    rowSelection?: boolean
    viewOptions?: boolean
  }
}

export function DataTable<TData, TValue>({
  data,
  columns,
  features = {
    sorting: true,
    filtering: true,
    pagination: true,
    columnVisibility: false,
    rowSelection: false,
    viewOptions: true,
  },
}: DataTableProps<TData, TValue>) {
  const table = useDataTable({
    data,
    columns,
    enableSorting: features.sorting,
    enableFiltering: features.filtering,
    enablePagination: features.pagination,
    enableColumnVisibility: features.columnVisibility,
    enableRowSelection: features.rowSelection,
  })

  return (
    <div className="flex w-full flex-col">
      <div className="flex items-center justify-between py-4">
        {features.filtering && <DataTableFilter table={table} />}
        {features.viewOptions && <DataTableViewOptions table={table} />}
      </div>
      <Table className="my-4">
        <TableHeader>
          {table.getHeaderGroups().map((headerGroup) => (
            <TableRow key={headerGroup.id}>
              {headerGroup.headers.map((header) => (
                <TableHead key={header.id}>
                  {header.isPlaceholder
                    ? null
                    : flexRender(
                        header.column.columnDef.header,
                        header.getContext()
                      )}
                </TableHead>
              ))}
            </TableRow>
          ))}
        </TableHeader>
        <DataTableBody table={table} />
      </Table>

      {features.pagination && <DataTablePagination table={table} />}
    </div>
  )
}

